    public static String obsfucateString(String input) {
        StringBuilder output = new StringBuilder(input.length());
        for (int i = 0; i < input.length(); i++) {
            if (input.charAt(i) == ' ') {
                output.append(" ");
            } else {
                output.append(validChars.charAt((int)(Math.random() * validChars.length())));
            }
        }
        return output.toString();
    }
